import { createContext, useContext, useState } from "react";
import { executeBasicAuthenticationService } from "../api/HelloWorldApiService";

// Create a context and export it so that it is accessible to other components
export const AuthContext = createContext()
export const useAuth = () => useContext(AuthContext)

// share the context with other components, children is all the components under AuthProvider
export default function AuthProvider({ children }) {

    // put some state in the context
    const [isAuthenticated, setIsAuthenticated] = useState(false)

    const [username, setUsername] = useState(null)

    function login(username, password) {
        if (username==='Abhi' && password==='patel') {
            setIsAuthenticated(true)
            setUsername(username)
            return true
        } else {
            setIsAuthenticated(false)
            setUsername(null)
            return false
        }
    }

    // function login(username, password) {

    //     const token = "Basic " + window.btoa(username+":"+password)

    //     executeBasicAuthenticationService(token)
    //         .then(response => console.log(response))
    //         .catch(error => console.log(error))

    //         setIsAuthenticated(false)

    //     // if (username==='Abhi' && password==='patel') {
    //     //     setIsAuthenticated(true)
    //     //     setUsername(username)
    //     //     return true
    //     // } else {
    //     //     setIsAuthenticated(false)
    //     //     setUsername(null)
    //     //     return false
    //     // }
    // }

    function logout() {
        setIsAuthenticated(false)
    }

    return (
        <AuthContext.Provider value={ {isAuthenticated, login, logout, username} }>
            {children}
        </AuthContext.Provider>
    );
}
