import { useState } from 'react';
import { BrowserRouter, Routes, Route, useNavigate, useParams, Link } from 'react-router-dom';
import './TodoApp.css'
import HeaderComponent from './HeaderComponent'
import LogoutComponent from './LogoutComponent'

export default function TodoApp () {
    return (
        <div className="TodoApp">
            <BrowserRouter>
                <HeaderComponent/>
                <Routes>
                    <Route path='/' element={<LoginComponent/>}/>
                    <Route path='/Login' element={<LoginComponent/>}/>
                    <Route path='/welcome/:username' element={<WelcomeComponent/>}/>
                    <Route path='/todos' element={<ListTodoComponent/>}/>
                    <Route path='/logout' element={<LogoutComponent/>}/>
                    <Route path='*' element={<ErrorComponent/>}/>
                </Routes>
            </BrowserRouter>
        </div>
    );
}

function LoginComponent () {

    const [username,setUsername] = useState('Abhi')
    const [password,setPassword] = useState('')
    const [showSuccess,setSuccessMessage] = useState(false)
    const [showError,setErrorMessage] = useState(false)
    const navigate = useNavigate()

    function handleUsernameChange(event) {
        setUsername(event.target.value)
    }

    function handlePasswordChange(event) {
        setPassword(event.target.value)
    }

    function handleSubmit() {
        if (username==='Abhi' && password==='patel') {
            setErrorMessage(false)
            setSuccessMessage(true)
            navigate(`/welcome/${username}`)
        } else {
            setSuccessMessage(false)
            setErrorMessage(true)
        }
    }

    return (
        <div className="LoginComponent">
            <h1>Login</h1>
            {showSuccess && <div className='successMessage'>Authenticated Successfully</div>}
            {showError && <div className='errorMessage'>Authentication Failed. Please check your credentials</div>}
            <div className="LoginForm">
                <div>
                    <label>Username</label>
                    <input type="text" name="username" value={username} onChange={handleUsernameChange}/>
                </div>
                <div>
                    <label>Password</label>
                    <input type="password" name="password" value={password} onChange={handlePasswordChange}/>
                </div>
                <div>
                    <button type="button" name="login" onClick={handleSubmit}>Login</button>
                </div>
            </div>
        </div>
    );
}

function WelcomeComponent () {

    const {username} = useParams()

    return (
        <div className="WelcomeComponent">
            <h1>Welcome {username}</h1>
            <div>
                Manage your todos. <Link to="/todos">Click here</Link>
            </div>
        </div>
    );
}



function ListTodoComponent () {

    const today = new Date()
    const targetDate = new Date(today.getFullYear()+12, today.getMonth(), today.getDay())

    const todos = [
        {id: 1, description: 'Learn AWS', done: false, targetDate: targetDate},
        {id: 2, description: 'Spring Boot certification', done: false, targetDate: targetDate},
        {id: 3, description: 'Learn Azure', done: false, targetDate: targetDate}
    ]

    return (
        <div className='container'>
            <h1>Things that you want to do!</h1>
            <div>
                <table className='table'>
                    <thead>
                        <tr>
                            <td><b>Id</b></td>
                            <td><b>Description</b></td>
                            <td><b>Is Done?</b></td>
                            <td><b>Target Date</b></td>
                        </tr>
                    </thead>
                    <tbody>
                    {
                        todos.map(
                            todo => (
                                <tr key={todo.id}>
                                    <td>{todo.id}</td>
                                    <td>{todo.description}</td>
                                    <td>{todo.done.toString()}</td>
                                    <td>{todo.targetDate.toDateString()}</td>
                                </tr>
                            )
                        )
                    }
                    </tbody>
                </table>
            </div>
        </div>
    );
}
