import { useState } from 'react';
import { PropTypes } from 'prop-types';
import './Counter.css'

export default function Counter () {
    const [totalCount, setCount] = useState(0)

    function incrementCounterParentFunction (by) {
        setCount(totalCount+by)
    }

    function decrementCounterParentFunction (by) {
        setCount(totalCount-by)
    }
    return (
    <>
        <span className="totalCount">{totalCount}</span>
        <CounterButton by={1} incrementMethod="incrementCounterParentFunction"/>
        <CounterButton by={2} incrementMethod="incrementCounterParentFunction"/>
        <CounterButton by={5} incrementMethod="incrementCounterParentFunction"/>
    </>
    );
}

function CounterButton ({by, incrementMethod}) {

    // [0,f]
    const [count, setCount] = useState(0)

    function incrementCounter () {
        setCount(count+by)
        incrementMethod(by)
    }

    function decrementCounter () {
        if ((count-by)>=0) {
            setCount(count-by)
            incrementMethod(by)
        }
    }

    return (
        <div className="CounterButton">
            <span className="count">{count}</span>
            <div>
                <button className="counterButton" onClick={incrementCounter}>+{by}</button>
                <button className="counterButton" onClick={decrementCounter}>-{by}</button>
            </div>
        </div>
    );
}

CounterButton.propTypes = {
    by: PropTypes.number
}

CounterButton.defaultProps = {
    by: 1
}
