import { useState } from 'react';
import { PropTypes } from 'prop-types';
import './Counter.css'

export default function CounterButton ({by}) {

    // [0,f]
    const [count, setCount] = useState(0)

    function incrementCounter () {
        setCount(count+by)
        console.log('incrementCounter clicked')
    }

    function decrementCounter () {
        if ((count-by)>=0) {
            setCount(count-by)
        }
        console.log("decrementCounter clicked")
    }

    return (
        <div className="CounterButton">
            <span className="count">{count}</span>
            <div>
                <button className="counterButton" onClick={incrementCounter}>+{by}</button>
                <button className="counterButton" onClick={decrementCounter}>-{by}</button>
            </div>
        </div>
    );
}

CounterButton.propTypes = {
    by: PropTypes.number
}

CounterButton.defaultProps = {
    by: 1
}
